import React,{useState} from 'react';
import BtnComponents from './Components/BtnComponents';
import DisplayComponents from './Components/DisplayComponents';
import './App.css';

function App() {
  const [time,setTime]= useState({ms:0,s:0,m:0,h:0});
  const[interv,setInterv] = useState();
  const[state,setState] = useState(0);
  const start = () =>{ 
     run();
     setState(1);
     setInterv(setInterval(run,10));
  };
  var updateMs = time.ms,updateS= time.s,updateM = time.m,updateH = time.h;

  const run = () => {
    if(updateM === 60){
      updateH++;
      updateM = 0;
    }
    if(updateS === 60){
      updateM++;
      updateS = 0;
    }
    if(updateMs === 100){
      updateS++;
      updateMs = 0;
    }
    updateMs++;
    return setTime({ms:updateMs,s:updateS,m:updateM,h:updateH})
  };

  const stop = () =>{ 
    clearInterval(interv);
    setState(2);
 };

 const reset = () =>{ 
  clearInterval(interv);
  setState(0);
  return setTime({ms:0,s:0,m:0,h:0})
};

  return (
    <div className="main_section">
     <div className="clock-holder">
     <div className="stopwatch">
        <DisplayComponents time={time}/>
        <BtnComponents reset={reset} stop={stop} state={state} start={start}/>
        <DisplayComponents time={time}/>
        <BtnComponents reset={reset} stop={stop} state={state} start={start}/>

     </div>
    </div>
    </div>

  );






}



export default App;
