import React ,{Component} from 'react';
import{ Button,Form,FromGroup,Label,Input, FormGroup} from 'reactstrap';
import './App.css';

class App extends Component {
    constructor()
    {
        super();
        this.state={
        email:null,
        password:null,
        login:false,
        store:null
        }
    }
  login()
  {
      fetch('http://localhost:3001/api/auth/login', { mode: 'no-cors' },{
          method:"POST",
          body:JSON.stringify(this.state)

      }).then((response) => { 
          response.json().then((result)=>{
              console.warn("result",result);
              localStorage.setItem('login',JSON.stringify({
                  login:true,
                  token:result.token

              }))

          })

      })
    }


    render(){
    return(
        <div className="bar">
       <Form  className ="login-form">
       <h1 className="font-weight-bold"> mywebside.com  </h1>
       <h2>Welcome</h2>
       <FormGroup>
        <Label>Email</Label>
         <input type="email"  onChange={(event)=>{this.setState({email:event.target.value})}}></input>
        </FormGroup>
        <FormGroup>
        <Label>Password</Label>
         <input type="password"  onChange={(event)=>{this.setState({password:event.target.value})}} ></input>
        </FormGroup>
        <Button onClick ={()=>{this.login()}} className="btn-lg btn-dark btn-block">Login</Button>
      </Form>
      </div>

      );
      }
    }
    export default App;
