import React,{useState} from 'react';
import BtnComponents from './Components/BtnComponents';
import BtnComponents2 from './Components/BtnComponents2';
import DisplayComponents from './Components/DisplayComponents';
import DisplayComponents2 from './Components/DisplayComponents2';
import './App.css';

function App() {
  const [time,setTime]= useState({ms:0,s:0,m:0,h:0});
  const[interv,setInterv] = useState();
  const [time2,setTime2]= useState({ms:10,s:10,m:10,h:0});
  const[interv2,setInterv2] = useState2();

  const[state,setState] = useState(0);
  const start = () =>{ 
     run();
     setState(1);
     setInterv(setInterval(run,10));
  };
  const start2 = () =>{ 
    run2();
    setState2(1);
    setInterv(setInterval(run,10));
 };

  var updateMs = time.ms,updateS= time.s,updateM = time.m,updateH = time.h;

  const run = () => {

    if(updateS === 10){
      updateM++;
      updateS = 0;
    }
    if(updateMs === 10){
      updateS++;
      updateMs = 0;
    }
    updateMs++;
    return setTime({ms:updateMs,s:updateS,m:updateM,h:updateH})
  };
  const run2 = () => {

    if(updateS === 10){
      updateM++;
      updateS = 0;
    }
    if(updateMs === 10){
      updateS++;
      updateMs = 0;
    }
    updateMs++;
    return setTime2({ms:updateMs,s:updateS,m:updateM,h:updateH})
  };


  const stop = () =>{ 
    clearInterval(interv);
    setState(2);
 };

 const stop2 = () =>{ 
  clearInterval(interv2);
  setState2(2);
};


 const reset = () =>{ 
  clearInterval(interv);
  setState(0);
  return setTime({ms:0,s:0,m:0,h:0})
};

const reset2 = () =>{ 
  clearInterval(interv2);
  setState2(0);
  return setTime2({ms:0,s:0,m:0,h:0})
};

  return (
    <div className="main_section">
     <div className="clock-holder">

     <div className="stopwatch">
        <DisplayComponents time={time}/>
        <BtnComponents reset={reset} stop={stop} state={state} start={start} />

     </div>
     <div className="stopwatch">
        <DisplayComponents2 time={time2}/>
        <BtnComponents2 reset={reset2} stop={stop2} state={state2} start2={start2} />

     </div>


    </div>
    </div>


  );



}





export default App;
