import React, { useState, useEffect } from 'react';

const Timer = () => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);

  function toggle() {
    setIsActive(!isActive);
  }

  function reset() {
    setSeconds(0);
    setIsActive(false);
  }

  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setSeconds (seconds + 1);
      }, 100);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  return (
    <div>
    <h1>Timer 1 </h1>
      <div className="time">
        {0.1*seconds}s
      </div>
      <div className="row">
        <button className={` button-primary stopwatch-btn-gre button -${isActive ? 'active' : 'inactive'}`} onClick={toggle}>
          {isActive ? 'Stop' : 'Start'}
        </button>
        <button className="stopwatch-btn-gre button" onClick={reset}>
          Reset
        </button>
      </div>
    </div>
  );
};

export default Timer;
