import React, { useState, useEffect,Component } from 'react';


const Timer = () => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const num1 = seconds/10;

  function toggle() {
    setIsActive(!isActive);
  }

  function reset() {
    setSeconds(0);
    setIsActive(false);
  }


}


  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setSeconds (seconds => seconds + 1);
      }, 100);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  childFunction=(e)=>{
    e.preventDefault();
    this.props.functionCallFromParent("Hello From Child1");
  return (
    <div>
    <h1>Timer 1 </h1>
      <div className="time">
        {seconds/10}s
        {num1}

      </div>
      <div className="row">
        <button className={` button-primary stopwatch-btn-gre button -${isActive ? 'active' : 'inactive'}`} onClick={toggle}>
          {isActive ? 'Stop' : 'Start'}

        </button>
        <button className="stopwatch-btn-gre button" onClick={reset}>
          Reset
        </button>
      </div>
      <div>   
                <button onClick={this.childFunction.bind(this)}>Click</button>
     </div>


    </div>
  );
};

export default Timer;
